import React, { useState, useRef, useEffect } from 'react';
import { PaperAirplaneIcon, SparklesIcon } from '@heroicons/react/24/solid';
import Message from './components/Message';
import SuggestedResponses from './components/SuggestedResponses';
import { Message as MessageType } from './types';
import { sendMessage } from './services/api';

// Suggested responses based on conversation state
const getSuggestedResponses = (messages: MessageType[]): string[] => {
  if (messages.length === 0) return [];
  
  const lastMessage = messages[messages.length - 1];
  
  // If the last message is from the agent, suggest responses based on its content
  if (lastMessage.sender === 'agent') {
    const content = lastMessage.content.toLowerCase();
    
    if (content.includes('how can i help')) {
      return [
        "Tell me about yourself",
        "What services do you offer?",
        "I need help with a problem"
      ];
    }
    
    if (content.includes('name')) {
      return [
        "My name is John",
        "I'm Sarah",
        "Call me Alex"
      ];
    }
    
    if (content.includes('feeling')) {
      return [
        "I'm feeling great today!",
        "I'm a bit stressed",
        "I'm feeling neutral"
      ];
    }
  }
  
  // Default suggestions
  return [
    "Tell me more",
    "That's interesting",
    "I have another question"
  ];
};

function App() {
  const [messages, setMessages] = useState<MessageType[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [suggestedResponses, setSuggestedResponses] = useState<string[]>([]);

  // Add welcome message when component mounts
  useEffect(() => {
    const welcomeMessage: MessageType = {
      id: Date.now().toString(),
      content: "Hello! I'm your SIC Agent chatbot. How can I help you today?",
      sender: 'agent',
      timestamp: new Date(),
    };
    setMessages([welcomeMessage]);
  }, []);

  // Update suggested responses when messages change
  useEffect(() => {
    setSuggestedResponses(getSuggestedResponses(messages));
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async (messageText: string = inputMessage) => {
    if (!messageText.trim()) return;

    const userMessage: MessageType = {
      id: Date.now().toString(),
      content: messageText,
      sender: 'user',
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      const agentMessage = await sendMessage(messageText);
      setMessages(prev => [...prev, agentMessage]);
    } catch (error) {
      console.error('Error sending message:', error);
      // Add error message to chat
      const errorMessage: MessageType = {
        id: Date.now().toString(),
        content: 'Sorry, there was an error processing your message. Please try again.',
        sender: 'agent',
        timestamp: new Date(),
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleSuggestionClick = (suggestion: string) => {
    handleSendMessage(suggestion);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="max-w-4xl mx-auto p-4">
        <header className="bg-white shadow-sm rounded-lg p-4 mb-4 flex items-center">
          <SparklesIcon className="h-6 w-6 text-blue-500 mr-2" />
          <h1 className="text-2xl font-bold text-gray-800">SIC Agent Chatbot</h1>
        </header>
        
        <main className="bg-white rounded-lg shadow-sm p-4">
          <div className="h-[600px] overflow-y-auto mb-4 p-4 border rounded-lg bg-gray-50">
            {messages.map(message => (
              <Message key={message.id} message={message} />
            ))}
            {isLoading && (
              <div className="flex items-center justify-center">
                <div className="animate-pulse text-gray-500 flex items-center">
                  <div className="w-2 h-2 bg-blue-500 rounded-full mr-2 animate-bounce" style={{ animationDelay: '0ms' }}></div>
                  <div className="w-2 h-2 bg-blue-500 rounded-full mr-2 animate-bounce" style={{ animationDelay: '150ms' }}></div>
                  <div className="w-2 h-2 bg-blue-500 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>
          
          <div className="flex flex-col">
            <div className="flex gap-2">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Type your message..."
                className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                disabled={isLoading}
              />
              <button 
                onClick={() => handleSendMessage()}
                disabled={isLoading || !inputMessage.trim()}
                className="bg-blue-500 text-white p-2 rounded-lg hover:bg-blue-600 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <PaperAirplaneIcon className="h-5 w-5" />
              </button>
            </div>
            
            <SuggestedResponses 
              suggestions={suggestedResponses} 
              onSelect={handleSuggestionClick} 
            />
          </div>
        </main>
        
        <footer className="mt-4 text-center text-sm text-gray-500">
          <p>SIC Agent Chatbot - Powered by AI</p>
        </footer>
      </div>
    </div>
  );
}

export default App;
