import { Message } from '../types';
import { processMessage } from './chatbot';

const API_URL = 'https://sic-agent-app-adg1.onrender.com';

// Simple response templates for demo purposes
const RESPONSES = {
  greeting: [
    "Hello! How can I help you today?",
    "Hi there! What can I do for you?",
    "Greetings! How may I assist you?"
  ],
  farewell: [
    "Goodbye! Have a great day!",
    "See you later! Take care!",
    "Bye! Feel free to come back if you need more help!"
  ],
  thanks: [
    "You're welcome!",
    "Glad I could help!",
    "My pleasure! Is there anything else you need?"
  ],
  default: [
    "I'm not sure I understand. Could you rephrase that?",
    "I'm still learning. Could you try asking in a different way?",
    "I'm not sure about that. Can you provide more details?"
  ]
};

// Simple keyword matching for demo purposes
const getResponseType = (message: string): keyof typeof RESPONSES => {
  const lowerMessage = message.toLowerCase();
  
  if (lowerMessage.match(/^(hi|hello|hey|greetings)/)) {
    return 'greeting';
  }
  
  if (lowerMessage.match(/^(bye|goodbye|see you|farewell)/)) {
    return 'farewell';
  }
  
  if (lowerMessage.match(/^(thanks|thank you|appreciate it)/)) {
    return 'thanks';
  }
  
  return 'default';
};

// Get a random response from the appropriate category
const getRandomResponse = (type: keyof typeof RESPONSES): string => {
  const responses = RESPONSES[type];
  return responses[Math.floor(Math.random() * responses.length)];
};

export const sendMessage = async (message: string): Promise<Message> => {
  try {
    // Use our local chatbot service
    return await processMessage(message);
    
    // In a real app, you would call the API here
    // const response = await axios.post(`${API_URL}/chat`, { message });
    // return {
    //   id: Date.now().toString(),
    //   content: response.data.response,
    //   sender: 'agent',
    //   timestamp: new Date(),
    // };
  } catch (error) {
    console.error('Error sending message:', error);
    throw error;
  }
};
