// SIC Life Knowledge Base

export interface KnowledgeItem {
  id: string;
  category: string;
  subcategory?: string;
  question: string;
  answer: string;
  keywords: string[];
  relatedQuestions?: string[];
}

export interface InsuranceProduct {
  id: string;
  name: string;
  type: string;
  description: string;
  benefits: string[];
  coverageAmount: {
    min: number;
    max: number;
  };
  premiumRange: {
    min: number;
    max: number;
  };
  eligibility: {
    minAge: number;
    maxAge: number;
    requirements: string[];
  };
  documents: string[];
}

export interface Branch {
  id: string;
  name: string;
  address: string;
  phone: string;
  email: string;
  location: {
    lat: number;
    lng: number;
  };
  workingHours: {
    weekdays: string;
    saturday: string;
    sunday: string;
  };
}

// Insurance Products Data
export const insuranceProducts: InsuranceProduct[] = [
  {
    id: 'universal-life',
    name: 'Education Plan Plus',
    type: 'Universal Life',
    description: 'A flexible education policy that helps secure your child\'s academic future while providing life coverage.',
    benefits: [
      'Flexible premium payments',
      'Cash value accumulation',
      'Education funding guarantee',
      'Life insurance protection'
    ],
    coverageAmount: {
      min: 10000,
      max: 1000000
