    ],
    coverageAmount: {
      min: 50000,
      max: 2000000
    },
    premiumRange: {
      min: 50,
      max: 1000
    },
    eligibility: {
      minAge: 18,
      maxAge: 65,
      requirements: [
        'Medical examination',
        'Proof of income',
        'Valid ID'
      ]
    },
    documents: [
      'Medical report',
      'Valid ID',
      'Proof of income'
    ]
  }
];

// Branch Information
export const branches: Branch[] = [
  {
    id: 'head-office',
    name: 'Head Office',
    address: 'No. 1 Jones Nelson Road, Adabraka Freetown, Accra',
    phone: '+233-302-662286',
