    location: {
      lat: 5.5602,
      lng: -0.2174
    },
    workingHours: {
      weekdays: '8:00 AM - 5:00 PM',
      saturday: '9:00 AM - 2:00 PM',
      sunday: 'Closed'
    }
  }
];

// FAQ Knowledge Base
export const sicLifeKnowledge: KnowledgeItem[] = [
  // Product Information
  {
    id: 'prod-1',
    category: 'Products',
    subcategory: 'Universal Life',
    question: 'What is the Education Plan Plus?',
    answer: 'Education Plan Plus is a flexible education policy that helps secure your child\'s academic future while providing life coverage. It offers flexible premium payments, cash value accumulation, and guaranteed education funding.',
    keywords: ['education', 'plan', 'universal', 'life', 'child', 'school']
  },
  
  // Claims Process
  {
    id: 'claims-1',
    category: 'Claims',
    question: 'How do I file a claim?',
    answer: 'To file a claim, follow these steps:\n1. Gather required documents (policy document, claim form, supporting documents)\n2. Visit our e-claims portal or nearest branch\n3. Submit your claim form and documents\n4. Receive your claim reference number\n5. Track your claim status online',
    keywords: ['claim', 'file', 'process', 'submit']
  },
  
  // Premium Payments
